var get_penalties = function(callback){
     $.get( "<?php echo base_url();?>index.php/reports/get_penalty", function( data ) {
        data = $.parseJSON(data);
        callback(data);
     });
}

var get_violations_inspectors = function(callback){
     $.get( "<?php echo base_url();?>index.php/reports/get_violations", function( violations ) {
        var violations = $.parseJSON(violations);
        $.get( "<?php echo base_url();?>index.php/reports/get_inspectors", function( inspectors ) {
        var inspectors = $.parseJSON(inspectors);
        inspectors = inspectors.filter(function(el){
            return el.designation == '2';
        });
        // console.log(violations);
        
        inspectors.map(function(el, index, arr){
            el['violations'] = 0;
            el['penalty'] = 0;
            for (var i = 0; i < violations.length; i++) {
                       if(el.id == violations[i].id_inspector){
                        el['violations'] = el['violations']  + 1;
                        el['penalty'] = el['penalty'] +  parseInt(violations[i].penalty);
                       } 
            }            
        });
        // console.log(inspectors);
        callback(inspectors);

     });
     });
}

$(function() {
    get_violations_inspectors(function(inspectors){
        var xaxis = inspectors.map(function(el, index, arr){
            return el.name;
        });
         var yaxis = inspectors.map(function(el, index, arr){
            return el.violations;
        });
        
         xaxis.unshift(null);
         yaxis.unshift('Violations');
         // console.log(yaxis);
        $('#inspector_violation_bar_chart').highcharts({

        chart: {
            type: 'column'
        },

        title: {
            text: 'Violations Reported by Inspectors'
        },
        credits: false,
        xAxis: {
            labels: {
                rotation: -45,
                style: {
                    fontSize: '13px',
                    fontFamily: 'Verdana, sans-serif'
                }
            }
        },

        data: {
            columns: [
                xaxis, // categories
                yaxis
            ]
        }
    });
    });
    
});

