$(function() {
    get_penalties(function(data){
        
        dates = data.map(function(el, index, arr){
            return el.doa.split("-")[0];
        });
        dates = dates.filter(function(item, pos) {
        return dates.indexOf(item) == pos;
        });
        
     var get_section_wise = function(s){
       total = {};
         for (var i = 0; i < dates.length; i++) {
             total[dates[i]] = 0;
         }
         data.map(function(el, index, arr){
            if(el.section == s){
            total[el.doa.split("-")[0]] = total[el.doa.split("-")[0]] + parseInt(el.penalty);    
            }
        });
         ans = [];
         for (var i = 0; i < dates.length; i++) {
             ans.push(total[dates[i]]);
         }
        return ans;
     }   
        

       var housing = get_section_wise(1);
       var health = get_section_wise(2);
       var market = get_section_wise(3);
        console.log(health);

        $('#yearly_penalty_summary').highcharts({
        chart: {
            type: 'column'
        },
        title: {
            text: 'Yearly Penalty Summary'
        },
        credits: {
            enabled: false
        },
        xAxis: {
            categories: dates
        },
        series: [{
            type: 'column',
            name: 'Housing Inspections',
            data: housing
        }, {
            type: 'column',
            name: 'Environmental and Health Inspections',
            data: health
        }, {
            type: 'column',
            name: 'Market Control Inspections',
            data: market
        }],
        tooltip: {
            pointFormat: '{series.name}: <b>{point.y}</b><br/>'
        }
    });
    });
    
});

